        {
            commit("CATEGORYLIST",result.data)
        }
    }
}
//修改数据的唯一途径
const mutations={
    CATEGORYLIST(state,categoryList){
        state.categoryList=categoryList
    }
}
//存储共享数据
const state={
    //state中的默认值
