import {reqGoodsInfo} from "../../api"
const actions={
   async getGoodInfo({commit},skuId){
       let result= await reqGoodsInfo(skuId)
       if(result.code==200){
        commit("getGoodInfo",result.data)
       }
    }
}
const mutations={
    getGoodInfo(state,data){
        state.goodInfo=data
    }
}
const state={
    goodInfo:{}
}
const getters={
    categoryView(){
        //
        return state.goodInfo.categoryView || {}
    }

}
export default{
    actions,
    mutations,
    state,
    getters
}
