import {reqGetCode} from '@/api'
const actions={
    //获取验证码
   async getCode({commit},phone){
      let result=await reqGetCode(phone)
      if(result.code==200)
      {
        commit('getCode',result.data)
        return '0k'
      }else{
        return Promise.reject(new Error('faile'))
      }
    }
}
const mutations={
    getCode(state,code){
        state.code=code;
    }
}
const state={
    code:''
}
const getters={}
export default{
    state,
    actions,
    getters,
    mutations
}
