import { Promise } from 'core-js'
import {reqGetCarList,reqDeleteCartById,reqUpdateCheckById} from '../../api'
const actions={
    async getShopCarList({commit}){
        let result= await reqGetCarList()
        if(result.code==200)
        {
            commit('getShopCarList',result.data)
        }
    },
    async deleteCart({commit},skuId){
        let result= await reqDeleteCartById(skuId)
        if(result.code==200)
        {
            return 'ok'
        }else{
            return Promise.reject(new Error('faile'))
        }
    },
    async reqUpdateCheckById({commit},{skuId,}){
        let result= await reqDeleteCartById(skuId)
        if(result.code==200)
        {
            return 'ok'
        }else{
            return Promise.reject(new Error('faile'))
        }
    }
}
const mutations={
    getShopCarList(state,data){
        state.shopCarList=data
    }
}
const state={
    shopCarList:[]
}
const getters={
    carList(state){
        return state.shopCarList[0] || {}
    }
}
export default{
    actions,
    mutations,
    state,
    getters
}
