import {reqGetCode} from '@/api'
const actions={
    //获取验证码
   async getCode({commit},phone){
      let result=await reqGetCode(phone)
      if(result==200)
      {
        commit('getCode',result.data)
        return '0k'
      }else{
        return Promise.reject
      }
    }
}
const mutations={}
const state={}
const getters={}
export default{
    state,
    actions,
    getters,
    mutations
}
