import Vue from "vue";
import VueRouter from "vue-router";
Vue.use(VueRouter);
//引入路由组件
import Home from '../pages/Home'
import Login from '../pages/Login'
import Search from '../pages/Search'
import Register from '../pages/Register'
//配置路由
const router=new VueRouter({
    routes:[
        {
            path:"/home",
            component:Home,
            meta:{show:true}
        },
        {
            path:"/Login",
            component:Login,
            meta:{show:false}
        },
        {
            path:"/Search/:keyword？",
            component:Search,
            meta:{show:true},
            name:'search'
        },
        {
            path:"/Register",
            component:Register,
            meta:{show:false}
        },
        {//重定向
            path:"*",
            redirect:'/home'
        }
    ]
})
export default router
//  注册完路由，所有组件都有$route $router
