import {reqGetCode,register,reqUserLogin,} from '@/api'
const actions={
    //获取验证码
   async getCode({commit},phone){
      let result=await reqGetCode(phone)
      if(result.code==200)
      {
        commit('getCode',result.data)
        return '0k'
      }else{
        return Promise.reject(new Error('faile'))
      }
    },
    //完成注册
   async userRegister({commit},user){
       let result=await register(user)
       if(result.code=200)
       {
        return 'ok'
       }else{
        return Promise.reject(new Error('faile'))
       }
    },
    //登录--token--服务器下发，前台持久化token 并且拿着token向服务器请求数据
    async userLogin({commit},params){
       let result= await reqUserLogin(params)
       if(result.code=200)
       {
        commit('userLogin',result.data)
        return 'ok'
       }else{
        return Promise.reject(new Error('faile'))
       }
    },
    //token 获取用户信息
    async getUserInfo({commit},params){
        let result= await getUserInfo(params)
        if(result.code=200)
        {
         commit('userLogin',result.data)
         return 'ok'
        }else{
         return Promise.reject(new Error('faile'))
        }
     }
}
const mutations={
    getCode(state,code){
        state.code=code;
    },
    userLogin(state,token){
        state.token=token
    }
}
const state={
    code:'',
    token:''
}
const getters={}
export default{
    state,
    actions,
    getters,
    mutations
}
