import {reqGetCarList} from '../../api'
const actions={
    async getShopCarList({commit}){
        let result= await reqGetCarList()
        if(result.code==200)
        {
            commit('getShopCarList',result.data)
        }
    }
}
const mutations={
    getShopCarList(state,data){
        state.shopCarList=data
    }
}
const state={
    shopCarList:[]
}
const getters={
    carList(state){
        return state.shopCarList
    }
}
export default{
    actions,
    mutations,
    state,
    getters
}
