//引入路由组件
import Home from '../pages/Home'
import Login from '../pages/Login'
import Search from '../pages/Search'
import Register from '../pages/Register'
import Detail from '../pages/Detail'
import AddCartSuccess from '../pages/AddCartSuccess'
import ShopCart from '../pages/ShopCart'
import Trade from '@/pages/Trade'
import Pay from '@/pages/Pay'
import PaySuccess from '@/pages/PaySuccess'
import Center from '@/pages/Center'
//路由信息
  export default  [
        {
            path:"/home",
            component:Home,
            meta:{show:true}
        },
        {
            path:"/Login",
            component:Login,
            meta:{show:false}
        },
        {
            path:"/Search/:keyword?",//指定params 参数keyword 可传可不传
            component:Search,
            meta:{show:true},
            name:'Search',
            //props 简化路由使用参数    
            //props:true //只能传params 参数
            //props:{a:'a'} 额外传参
            // props(){
            //     return{

            //     }
            // }能传params query参数
            
        },
        {
            path:"/Register",
            component:Register,
            meta:{show:false}
        },
        {
            path:"/Detail/:skuId",
            component:Detail,
            meta:{show:true}
        },
        {
            path:'/AddCartSuccess',
            component:AddCartSuccess,
            meta:{show:true},
            name:'AddCartSuccess'
        },
        {
            path:'/ShopCart',
            component:ShopCart,
            meta:{show:true},
            name:'ShopCart'
        },
        {
            path:'/trade',
            component:Trade,
            meta:{show:true},
            name:'trade'
        },
        {
            path:'/paysuccess',
            component:PaySuccess,
            meta:{show:true},
            name:'paysuccess'
        },
        {
            path:'/pay',
            component:Pay,
            meta:{show:true},
            name:'pay'
        },
        {
            path:'/center',
            component:Center,
            meta:{show:true},
            name:'center'
        },
        {//重定向
            path:"*",
            redirect:'/home'
        }
    ]
