import {requests} from "@/api/request"
const action={
   async getGoodInfo({commit},skuId){
       let result= await requests(skuId)
       if(result.code==200){
        commit("getGoodInfo",result)
       }
    }
}
const mutations={
    getGoodInfo(state,data){
        state.goodInfo=data
    }
}
const state={
    goodInfo
}
const getters={}
export default{
    action,
    mutations,
    state,
    getters
}
