//表单验证区域
import vue from 'vue'
import validate from 'vee-validate'
import zh_CN from 'vee-validate/dist/locale/zh_CN.json'
Vue.use(validate)
//表单验证
validate.Validator.localize('zh_CN',{
    messages:{
        ...zh_CN.messages,
        is:(field) => `${field}必须与密码相同`//修改内置规则的messag,让确认密码和密码相同
    },
    attributes:{//给校验的filed属性映射中文名称
        phone:'手机号',
        code:'验证码',
        password:'确认密码',
        isCheck
    }
})
